using UnityEngine; using System.Collections; using System.Collections.Generic;  [System.Serializable] public class GameData {
    /// <summary>     /// This class defines the formatting of player data for the whole experiment.     /// This is a simplified, general purpose codebase for creating different behavioural experiments in Unity.     /// Author: Hannah Sheahan, sheahan.hannah@gmail.com     /// Date: November 2018     /// </summary> 
    public string experimentVersion;     public string participantID;     public string participantAge;     public int participantGender;     public string confirmationCode;     public string participantFeedback;     public int totalTrials;     public float dataRecordFrequency;     public float restbreakDuration;     public float getReadyDuration;      public List<int> trialList = new List<int>();      public TrialData[] allTrialData;      // ********************************************************************** //     // Use a constructor      public GameData(int trials)     {         //  initialize array of trials, and instantiate each trial in the array         allTrialData = new TrialData[trials];         for (int i = 0; i < allTrialData.Length; i++)         {             allTrialData[i] = new TrialData();         }     } }  